using Oceania.Common.Data;
using Oceania.Common.Model;
using Oceania.Data.Redis;
using Oceania.ShopConfigService.Models.DataBase;

using WEF;
using WEF.Expressions;

namespace [namespace]
{
    /// <summary>
    /// 数据操作类
    /// </summary>
    public class [className]Operator : BaseOperator<[className]>
    {
        /// <summary>
        /// 市场数据操作类,
        /// 使用默认的配置
        /// </summary>
        public [className]Operator() : base()
        {

        }

        /// <summary>
        /// 添加实体
        /// </summary>
        /// <param name="entity"></param>
        /// <returns></returns>
        public bool Add([className] entity)
        {
            return BaseRepository.Insert(entity) > 0;
        }

        /// <summary>
        /// 更新
        /// </summary>
        /// <param name="entity"></param>
        /// <returns></returns>
        public bool Update([className] entity)
        {
            return BaseRepository.Update(entity) > 0;
        }

        /// <summary>
        /// 获取
        /// </summary>
        /// <param name="id"></param>
        /// <returns></returns>
        public [className] Get(string id)
        {
            return BaseRepository.Search().Where(b => b.ID == id).First();
        }


        /// <summary>
        /// 获取分页列表
        /// </summary>
        /// <param name="pagination"></param>
        /// <returns></returns>
        public PagedList<[className]> GetList(Pagination pagination)
        {
            PagedList<[className]> result = new PagedList<[className]>()
            {
                PageIndex = pagination.PageIndex,
                PageSize = pagination.PageSize
            };

            var count = BaseRepository.Search().Count();

            result.Total = count;

            if (count > 0)
            {
                var data = BaseRepository.DBContext.FromSql("select * from OC_Marketing", pagination.PageIndex, pagination.PageSize, pagination.OrderBy, !pagination.Desc).ToList<[className]>();

                result.List = data;
            }

            return result;
        }

        /// <summary>
        /// 删除
        /// </summary>
        /// <param name="id"></param>
        /// <returns></returns>
        public bool Delete(string id)
        {
            var entity = Get(id);

            if (entity != null)
            {
               return BaseRepository.Delete(entity)>0;
            }

            return false;
        }
    }
}

using System.Collections.Generic;
