namespace [namespace]
{
    /// <summary>
    /// [className]控制器
    /// </summary>
    [ApiController]
    [Authorize]
    [Route("api/[controller]/[action]")]
    public class [className]Controller : ControllerBase
    {
        [className]Service _marketingService;

        /// <summary>
        /// [className]控制器
        /// </summary>
        public [className]Controller()
        {
            _marketingService = new [className]Service();
        }

        /// <summary>
        /// 添加[className]
        /// </summary>
        /// <param name="entity"></param>
        /// <returns></returns>
        [HttpPost]
        public JsonResult<bool> Add[className]([FromBody][className] entity)
        {
            return _marketingService.Add[className](entity).ToJsonResult();
        }

        /// <summary>
        /// 修改[className]
        /// </summary>
        /// <param name="entity"></param>
        /// <returns></returns>
        [HttpPost]
        public JsonResult<bool> Update[className]([FromBody] [className] entity)
        {
            return _marketingService.Update[className](entity).ToJsonResult();
        }
        /// <summary>
        /// 获取[className]
        /// </summary>
        /// <param name="id"></param>
        /// <returns></returns>
        [HttpGet]
        public JsonResult<[className]> Get[className](string id)
        {
            _marketingService.Get[className](id).ToJsonResult();
        }
        /// <summary>
        /// 获取[className]分页列表
        /// </summary>
        /// <param name="pagination"></param>
        /// <returns></returns>
        [HttpGet]
        public JsonResult<PagedList<[className]>> Get[className]List(Pagination pagination)
        {
            //此处检查代码建议放置在service中，当前只是示例
            if (!pagination.RequiredCheck(out DataResult<PagedList<OCMarketing>> result))
            {
                return JsonResult<PagedList<OCMarketing>>.FromDataResult(result);
            }
            return _marketingService.Get[className]List(pagination).ToJsonResult();
        }

        /// <summary>
        /// 删除[className]
        /// </summary>
        /// <param name="id"></param>
        /// <returns></returns>
        [HttpPost]
        public JsonResult<bool> Delete[className](string id)
        {
            _marketingService.Delete[className](id).ToJsonResult();
        }
    }
}

